import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import AuthContext from "../context/AuthContext";
import ToastContext from "../context/ToastContext";

const CreateContact = () => {
  const { user } = useContext(AuthContext);
  const { toast } = useContext(ToastContext);

  const [userDetails, setUserDetails] = useState({
    name: "",
    username: "",
    email: "",
    address: {
      street: "",
      suite: "",
      city: "",
      zipcode: "",
      geo: {
        lat: "",
        lng: "",
      },
    },
    phone: "",
    website: "",
    company: {
      name: "",
      catchPhrase: "",
      bs: "",
    },
  });
  const navigate = useNavigate();

  const handleInputChange = (event) => {
    const { name, value } = event.target;

    setUserDetails({ ...userDetails, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const res = await fetch(`http://localhost:3000/api/create-contact`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(userDetails),
    });
    const result = await res.json();
    if (!result.error) {
      toast.success(`Created [${userDetails.name}] contact`);

      // Redirect back to Home with a callback function as a query parameter
      navigate(
        `/?updateContactList=${encodeURIComponent(JSON.stringify(result))}`
      );

      // Update only the provided fields
      setUserDetails({
        name: result.name || "",
        email: result.email || "",
        phone: result.phone || "",
        company: {
          name: result.companyName || "",
          catchPhrase: "",
          bs: "",
        },
      });
    } else {
      toast.error(result.error);
    }
  };

  return (
    <>
      <h2>Create your contact</h2>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="nameInput" className="form-label mt-4">
            Name Of Person
          </label>
          <input
            type="text"
            className="form-control"
            id="nameInput"
            name="name"
            value={userDetails.name}
            onChange={handleInputChange}
            placeholder="wenmeng"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="emailInput" className="form-label mt-4">
            Email Of Person
          </label>
          <input
            type="email"
            className="form-control"
            id="emailInput"
            name="email"
            value={userDetails.email}
            onChange={handleInputChange}
            placeholder="crliu@example.com"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="phoneInput" className="form-label mt-4">
            Phone Number Of Person
          </label>
          <input
            type="number"
            className="form-control"
            id="phoneInput"
            name="phone"
            value={userDetails.phone}
            onChange={handleInputChange}
            placeholder="586.493.6943 x140"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="companyNameInput" className="form-label mt-4">
            Company Name Of Person
          </label>
          <input
            type="text"
            className="form-control"
            id="companyNameInput"
            name="companyName"
            value={userDetails.companyName}
            onChange={handleInputChange}
            placeholder="amazon"
            required
          />
        </div>

        <input
          type="submit"
          value="Add Contact"
          className="btn btn-info my-4"
        />
      </form>
    </>
  );
};

export default CreateContact;
