import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import AuthContext from "../context/AuthContext";

const Home = () => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  const [loading, setLoading] = useState(false);
  const [contacts, setContacts] = useState([]);
  const [searchInput, setSearchInput] = useState("");

  useEffect(async () => {
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:3000/api/`);
      const result = await res.json();
      if (!result.error) {
        setContacts(result.contacts);
        setLoading(false);
      } else {
        console.log(result);
        setLoading(false);
      }
    } catch (err) {
      console.log(err);
    }
  }, []);

  // useEffect(() => {
  //   !user && navigate("/login", { replace: true });
  // }, [user, navigate]);

  const handleSearchSubmit = (event) => {
    event.preventDefault();

    const newSearchUser = contacts.filter((contact) =>
      contact.name.toLowerCase().includes(searchInput.toLowerCase())
    );
    console.log(newSearchUser);
    setContacts(newSearchUser);
  };

  return (
    <>
      <div>
        <h1> Your Contacts</h1>
        <hr className="my-4" />

        <table className="table table-hover">
          <thead>
            <tr classNmae="table-dark">
              <th scope="col">Name</th>
              <th scope="col">Username</th>
              <th scope="col">Email</th>
              <th scope="col">Address</th>
              <th scope="col">Phone</th>
              <th scope="col">Website</th>
              <th scope="col">Company</th>
              <th scope="col">Catch Phrase</th>
              <th scope="col">Business Strategy</th>
            </tr>
          </thead>
          <tbody>
            {contacts.map((contact) => (
              <tr key={contact.id}>
                <td>{contact.name}</td>
                <td>{contact.username}</td>
                <td>{contact.email}</td>
                <td>
                  {contact.address.street}, {contact.address.suite},{" "}
                  {contact.address.city}, {contact.address.zipcode}
                </td>
                <td>{contact.phone}</td>
                <td>
                  <a href={contact.website}>{contact.website}</a>
                </td>
                <td>{contact.company.name}</td>
                <td>{contact.company.catchPhrase}</td>
                <td>{contact.company.bs}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default Home;
